import express from 'express';
import {createError} from 'http-errors';

import Mroute from './routes/messagesRoutes';
import Blogroute from './routes/blogsRoutes';
import Userroute from './routes/usersRoutes';
import Commentroute from './routes/commentsRoutes';
import Authroute from './routes/auth.routes';


const server = express();

const port = process.env.PORT || 3000;

server.use(express.json());
server.use(express.urlencoded({ extended: false }));
server.get('/', (req, res) => res.status(200).json({ 
    message: 'Welcome to my site' 
}));
server.use('/', Mroute);
server.use('/', Blogroute);
server.use('/', Userroute);
server.use('/', Commentroute);
server.use('/', Authroute);


server.use(async (req, res) => {
    const error = await new Error('Not found');
    error.status = 404;
    res.send({status: error.status, message: error.message});
});


server.listen(port, console.log(`server listening on ${port}`));
export default server;

import express from 'express';
import {create,readAll,readById,update,deleteMessage} from '../controllers/messagesControllers';

const router = express.Router();
router.post('/message', create);
router.get('/message', readAll);
router.get('/message/:id', readById);
router.put('/message/:id', update);
router.delete('/message/:id', deleteMessage);
export default router;

import messages from '../models/messagesCollection';
import { v4 as uuidv4 } from 'uuid';


export const readAll = (req, res) => {
    if (!messages) {
        return res.status(404).json({
            status: 404,
            error: 'No message found',
        });
    }
    return res.status(200).json({
        status: 200,
        message: 'messages successfully retrieved',
        data: {
            messages,
        },
    });
};
export const readById = (req, res) => {
    const id = req.params.id;
    const message = messages.find((message) => {
        return message.id === id;
    });
    if (message) {
        return res.status(200).json({
            status: 200,
            message: 'message successfully retrieved',
            data: message,
        });
    }
    return res.status(404).json({
        status: 404,
        error: 'message not found',
    });

};
export const create = (req, res) => {
    const message = {
        id: uuidv4(),
        name:req.body.fullname,
        email: req.body.email,
        phone:req.body.phone,
        message: req.body.message
    };
    messages.push(message);
    return res.status(201).json({
        status: 201,
        message: 'message successfully created',
        data: message,
    });

};

export const update=(req,res)=>{
    const id = req.params.id;
    const message = messages.find((message) => {
        return message.id === id;
    });
    if(message){
        message.name= req.body.name;
        message.email=req.body.email;
        message.phone=req.body.phone;
        message.message=req.body.message;
        
        return res.status(200).json({
            status: 200,
            message: 'message successfully updated',
            data: message,
        });
    }
    return res.status(404).json({
        status: 404,
        error: 'message not found',
    });
    
}

export const deleteMessage=(req,res)=>{
    const id=req.params.id;
    const message = messages.find((message) => {
        return message.id === id;
    });
    if(message){
        var a = messages.indexOf(message);
        messages.splice(a, 1);
        return res.status(200).json({
            status: 200,
            message: 'message successfully deleted',
            data: message,
        });
    }
    return res.status(404).json({
        status: 404,
        error: 'message not found',
    });
}

